/**
 * Vue is a modern JavaScript library for building interactive web interfaces
 * using reactive data binding and reusable components. Vue's API is clean
 * and simple, leaving you to focus on building your next great project.
 */

window.Vue = require('vue');

Vue.prototype.trans = (string, args) => {
    let value = _.get(window.i18n, string);

    _.eachRight(args, (paramVal, paramKey) => {
        value = _.replace(value, `:${paramKey}`, paramVal);
    });
    return value;
};



/**
 * We'll load the axios HTTP library which allows us to easily issue requests
 * to our Laravel back-end. This library automatically handles sending the
 * CSRF token as a header based on the value of the "XSRF" token cookie.
 */

window.axios = require('axios');

window.axios.defaults.headers.common = {
    'X-CSRF-TOKEN': window.Laravel.csrfToken,
    'X-Requested-With': 'XMLHttpRequest'
};

let userRoles = window.Laravel.userRoles
let userPermissions = window.Laravel.userPermissions
Vue.prototype.auth = {}
Vue.prototype.auth.user = window.Laravel.user

Vue.prototype.auth.hasRole = (role) => {
    if(userRoles.includes('super'))
        return true
    else if(userRoles.includes(role))
        return true
    else
        return false
}

Vue.prototype.auth.hasPermission = (permission) => {
    if(userPermissions.includes('all-permissions'))
        return true
    else if(userPermissions.includes(permission))
        return true
    else
        return false
}

